-- إضافة أسئلة البطاقة 1: المُبْدِّل (Switch)
INSERT INTO grade11_game_questions 
(section_id, topic_id, lesson_id, question_text, question_type, choices, correct_answer, explanation, difficulty_level, points)
VALUES
('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd', 
'ما هو الجهاز الأساسي لربط أجهزة الحاسوب ببعضها البعض ضمن شبكة محلية واحدة (LAN)؟', 'multiple_choice',
'[{"id":"A","text":"المُوزِّع (Hub)"},{"id":"B","text":"المُبْدِّل (Switch)"},{"id":"C","text":"الموجه (Router)"},{"id":"D","text":"نقطة الوصول (Access Point)"}]'::jsonb,
'المُبْدِّل (Switch)', 'المُبْدِّل (Switch) هو الجهاز الأساسي لربط أجهزة الحاسوب ببعضها البعض ضمن شبكة محلية واحدة (LAN)، وهو أكثر ذكاءً وأداءً من المُوزِّع (Hub).', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd',
'في أي طبقة من طبقات نموذج OSI يعمل المُبْدِّل (Switch)؟', 'multiple_choice',
'[{"id":"A","text":"الطبقة الأولى فقط (Physical Layer)"},{"id":"B","text":"الطبقة الثانية فقط (Data Link Layer)"},{"id":"C","text":"الطبقتين الأولى والثانية"},{"id":"D","text":"الطبقة الثالثة (Network Layer)"}]'::jsonb,
'الطبقتين الأولى والثانية', 'يعمل المُبْدِّل (Switch) في طبقتين من نموذج OSI: الطبقة الفيزيائية (Physical Layer) وطبقة وصلة البيانات (Data Link Layer).', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd',
'كيف يحدد المُبْدِّل (Switch) الوجهة الصحيحة لإرسال البيانات؟', 'multiple_choice',
'[{"id":"A","text":"عن طريق عنوان IP"},{"id":"B","text":"عن طريق عنوان MAC"},{"id":"C","text":"عن طريق رقم المنفذ"},{"id":"D","text":"عن طريق البث إلى جميع الأجهزة"}]'::jsonb,
'عن طريق عنوان MAC', 'يستخدم المُبْدِّل عنوان MAC (Media Access Control) لتحديد الجهاز المستقبل الصحيح، حيث يحتفظ بجدول يربط بين عناوين MAC والمنافذ المتصلة بها.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd',
'ما هي الميزة الرئيسية للمُبْدِّل (Switch) مقارنة بالمُوزِّع (Hub)؟', 'multiple_choice',
'[{"id":"A","text":"أرخص في السعر"},{"id":"B","text":"يرسل البيانات فقط إلى الجهاز المعني"},{"id":"C","text":"يعمل بدون كهرباء"},{"id":"D","text":"يدعم الاتصال اللاسلكي"}]'::jsonb,
'يرسل البيانات فقط إلى الجهاز المعني', 'الميزة الرئيسية للمُبْدِّل هي قدرته على إرسال البيانات بشكل مباشر إلى الجهاز المستهدف فقط، بينما يرسل المُوزِّع البيانات إلى جميع الأجهزة المتصلة، مما يحسن الأمان والأداء.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd',
'ما اسم الجدول الذي يحتفظ به المُبْدِّل لربط عناوين MAC بالمنافذ؟', 'multiple_choice',
'[{"id":"A","text":"جدول التوجيه (Routing Table)"},{"id":"B","text":"جدول MAC (MAC Table)"},{"id":"C","text":"جدول ARP (ARP Table)"},{"id":"D","text":"جدول DNS (DNS Table)"}]'::jsonb,
'جدول MAC (MAC Table)', 'يحتفظ المُبْدِّل بجدول يسمى MAC Table يربط فيه عنوان MAC لكل جهاز برقم المنفذ (Port) المتصل به، مما يسمح له بتوجيه البيانات بذكاء.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd',
'إذا وصلت بيانات إلى المُبْدِّل من جهاز غير مسجل في جدول MAC، ماذا يفعل المُبْدِّل؟', 'multiple_choice',
'[{"id":"A","text":"يرفض البيانات"},{"id":"B","text":"يرسلها إلى جميع المنافذ عدا المنفذ المصدر"},{"id":"C","text":"يحفظها في الذاكرة"},{"id":"D","text":"يرسل رسالة خطأ"}]'::jsonb,
'يرسلها إلى جميع المنافذ عدا المنفذ المصدر', 'عندما يستقبل المُبْدِّل بيانات من عنوان MAC غير معروف، يقوم بعملية Flooding حيث يرسل البيانات إلى جميع المنافذ (عدا منفذ المصدر) ثم يتعلم عنوان MAC الجديد عندما يرد الجهاز المستهدف.', 'hard', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', 'eeae7842-37c4-4ab3-aa86-e1672d1f17bd',
'ما الفرق الأساسي بين المُبْدِّل (Switch) والموجه (Router)؟', 'multiple_choice',
'[{"id":"A","text":"المُبْدِّل يربط أجهزة في شبكة واحدة، والموجه يربط بين شبكات مختلفة"},{"id":"B","text":"المُبْدِّل لاسلكي والموجه سلكي"},{"id":"C","text":"المُبْدِّل أبطأ من الموجه"},{"id":"D","text":"لا يوجد فرق بينهما"}]'::jsonb,
'المُبْدِّل يربط أجهزة في شبكة واحدة، والموجه يربط بين شبكات مختلفة', 'المُبْدِّل (Switch) يعمل داخل شبكة محلية واحدة (LAN) ويستخدم عناوين MAC، بينما الموجه (Router) يربط بين شبكات مختلفة ويستخدم عناوين IP ويعمل في الطبقة الثالثة من نموذج OSI.', 'hard', 10);