-- إضافة أسئلة البطاقات 2-7
INSERT INTO grade11_game_questions 
(section_id, topic_id, lesson_id, question_text, question_type, choices, correct_answer, explanation, difficulty_level, points)
VALUES
-- البطاقة 2: المُوزِّع (Hub)
('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'ما هو المُوزِّع (Hub) في الشبكات؟', 'multiple_choice',
'[{"id":"A","text":"جهاز يربط شبكات مختلفة"},{"id":"B","text":"جهاز يوزع البيانات على جميع الأجهزة المتصلة"},{"id":"C","text":"جهاز يحول الإشارات الرقمية إلى تناظرية"},{"id":"D","text":"جهاز لاسلكي فقط"}]'::jsonb,
'جهاز يوزع البيانات على جميع الأجهزة المتصلة', 'المُوزِّع (Hub) هو جهاز بسيط يعمل على الطبقة الفيزيائية، يستقبل البيانات من أي منفذ ويرسلها إلى جميع المنافذ الأخرى دون تمييز.', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'في أي طبقة من نموذج OSI يعمل المُوزِّع (Hub)؟', 'multiple_choice',
'[{"id":"A","text":"الطبقة الفيزيائية (Physical Layer)"},{"id":"B","text":"طبقة وصلة البيانات (Data Link Layer)"},{"id":"C","text":"طبقة الشبكة (Network Layer)"},{"id":"D","text":"طبقة النقل (Transport Layer)"}]'::jsonb,
'الطبقة الفيزيائية (Physical Layer)', 'يعمل المُوزِّع في الطبقة الأولى (الطبقة الفيزيائية) فقط، حيث يتعامل مع الإشارات الكهربائية دون معالجة أو فهم محتوى البيانات.', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'ما هي المشكلة الأمنية الرئيسية في استخدام المُوزِّع (Hub)؟', 'multiple_choice',
'[{"id":"A","text":"سهولة اختراقه"},{"id":"B","text":"جميع الأجهزة تستقبل جميع البيانات"},{"id":"C","text":"يتطلب كلمة مرور"},{"id":"D","text":"يحتاج إلى تشفير خاص"}]'::jsonb,
'جميع الأجهزة تستقبل جميع البيانات', 'المشكلة الأمنية الرئيسية للمُوزِّع هي أنه يرسل جميع البيانات إلى جميع الأجهزة المتصلة، مما يسمح لأي جهاز بالتنصت على حركة البيانات الخاصة بالأجهزة الأخرى.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'لماذا يُعتبر المُوزِّع (Hub) أقل كفاءة من المُبْدِّل (Switch)؟', 'multiple_choice',
'[{"id":"A","text":"لأنه أغلى في السعر"},{"id":"B","text":"لأنه يسبب تصادمات في البيانات"},{"id":"C","text":"لأنه يحتاج إلى صيانة دورية"},{"id":"D","text":"لأنه يستهلك طاقة أكثر"}]'::jsonb,
'لأنه يسبب تصادمات في البيانات', 'المُوزِّع يرسل البيانات إلى جميع الأجهزة في نفس الوقت، مما يزيد من احتمالية حدوث تصادمات (Collisions) عندما يحاول جهازان أو أكثر إرسال البيانات في نفس الوقت، مما يقلل من كفاءة الشبكة.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'ما هو نطاق التصادم (Collision Domain) في شبكة تستخدم Hub؟', 'multiple_choice',
'[{"id":"A","text":"كل منفذ يمثل نطاق تصادم منفصل"},{"id":"B","text":"جميع الأجهزة المتصلة بالـ Hub في نطاق تصادم واحد"},{"id":"C","text":"لا يوجد نطاق تصادم في الـ Hub"},{"id":"D","text":"نطاق التصادم يعتمد على عدد الأجهزة"}]'::jsonb,
'جميع الأجهزة المتصلة بالـ Hub في نطاق تصادم واحد', 'في الشبكات التي تستخدم Hub، جميع الأجهزة المتصلة تشترك في نطاق تصادم واحد (Single Collision Domain)، مما يعني أن جميع الأجهزة يجب أن تتنافس على نفس قناة الاتصال، بينما في Switch كل منفذ له نطاق تصادم خاص به.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'ما هو نوع الإرسال الذي يستخدمه المُوزِّع (Hub) عند استقبال البيانات؟', 'multiple_choice',
'[{"id":"A","text":"Unicast (إرسال أحادي)"},{"id":"B","text":"Multicast (إرسال متعدد)"},{"id":"C","text":"Broadcast (إرسال إذاعي)"},{"id":"D","text":"Anycast (إرسال لأقرب جهاز)"}]'::jsonb,
'Broadcast (إرسال إذاعي)', 'المُوزِّع يستخدم طريقة الإرسال الإذاعي (Broadcast) حيث يرسل البيانات الواردة إلى جميع المنافذ المتصلة به دون تمييز، وكل جهاز يفحص البيانات ويقرر إذا كانت موجهة له أم لا.', 'hard', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '5b433326-2ee8-4155-a69b-20de2fc5b2c7',
'في أي سيناريو قد يكون استخدام Hub مناسباً؟', 'multiple_choice',
'[{"id":"A","text":"شبكة كبيرة بها مئات الأجهزة"},{"id":"B","text":"شبكة صغيرة جداً بميزانية محدودة ولا تتطلب أمان عالي"},{"id":"C","text":"شبكة تحتاج إلى سرعة عالية"},{"id":"D","text":"شبكة تحتوي على بيانات حساسة"}]'::jsonb,
'شبكة صغيرة جداً بميزانية محدودة ولا تتطلب أمان عالي', 'الـ Hub قد يكون مناسباً فقط للشبكات الصغيرة جداً (2-3 أجهزة) ذات الميزانية المحدودة والتي لا تحتاج إلى أداء عالي أو أمان قوي، ولكن في معظم الحالات الحديثة، يُفضل استخدام Switch لأنه أصبح رخيصاً نسبياً ويوفر أداء وأمان أفضل بكثير.', 'hard', 10),

-- البطاقة 3: الموجه (Router)
('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'ما هي الوظيفة الأساسية للموجه (Router)؟', 'multiple_choice',
'[{"id":"A","text":"توزيع البيانات داخل شبكة واحدة"},{"id":"B","text":"ربط شبكات مختلفة ببعضها"},{"id":"C","text":"تحويل الإشارات التناظرية إلى رقمية"},{"id":"D","text":"تخزين البيانات"}]'::jsonb,
'ربط شبكات مختلفة ببعضها', 'الموجه (Router) هو جهاز شبكي يعمل في الطبقة الثالثة (Network Layer) ويستخدم لربط شبكات مختلفة ببعضها وتوجيه حزم البيانات بينها باستخدام عناوين IP.', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'في أي طبقة من نموذج OSI يعمل الموجه (Router)؟', 'multiple_choice',
'[{"id":"A","text":"الطبقة الأولى (Physical Layer)"},{"id":"B","text":"الطبقة الثانية (Data Link Layer)"},{"id":"C","text":"الطبقة الثالثة (Network Layer)"},{"id":"D","text":"الطبقة الرابعة (Transport Layer)"}]'::jsonb,
'الطبقة الثالثة (Network Layer)', 'يعمل الموجه في الطبقة الثالثة (طبقة الشبكة - Network Layer) من نموذج OSI، حيث يتعامل مع عناوين IP ويتخذ قرارات التوجيه بناءً عليها.', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'ما نوع العنوان الذي يستخدمه الموجه لتوجيه البيانات؟', 'multiple_choice',
'[{"id":"A","text":"عنوان MAC"},{"id":"B","text":"عنوان IP"},{"id":"C","text":"عنوان البريد الإلكتروني"},{"id":"D","text":"رقم المنفذ (Port Number)"}]'::jsonb,
'عنوان IP', 'يستخدم الموجه عنوان IP (Internet Protocol Address) لتحديد المسار الأمثل لإرسال حزم البيانات من شبكة إلى أخرى، على عكس المُبْدِّل الذي يستخدم عنوان MAC.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'ما اسم الجدول الذي يستخدمه الموجه لتحديد مسار البيانات؟', 'multiple_choice',
'[{"id":"A","text":"جدول MAC (MAC Table)"},{"id":"B","text":"جدول التوجيه (Routing Table)"},{"id":"C","text":"جدول ARP (ARP Table)"},{"id":"D","text":"جدول DNS (DNS Table)"}]'::jsonb,
'جدول التوجيه (Routing Table)', 'يحتفظ الموجه بجدول يسمى Routing Table يحتوي على معلومات عن الشبكات المختلفة وأفضل المسارات للوصول إليها، ويستخدمه لاتخاذ قرارات توجيه حزم البيانات.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'ما هي خدمة NAT في الموجه؟', 'multiple_choice',
'[{"id":"A","text":"خدمة تحويل عناوين الشبكة"},{"id":"B","text":"خدمة حماية من الفيروسات"},{"id":"C","text":"خدمة تسريع الإنترنت"},{"id":"D","text":"خدمة تخزين البيانات"}]'::jsonb,
'خدمة تحويل عناوين الشبكة', 'NAT (Network Address Translation) هي خدمة في الموجه تقوم بتحويل عناوين IP الخاصة (Private IP) المستخدمة داخل الشبكة المحلية إلى عنوان IP عام (Public IP) واحد للاتصال بالإنترنت، مما يوفر عناوين IP ويحسن الأمان.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'ما الفرق بين التوجيه الثابت (Static Routing) والتوجيه الديناميكي (Dynamic Routing)؟', 'multiple_choice',
'[{"id":"A","text":"الثابت يدوي والديناميكي تلقائي"},{"id":"B","text":"الثابت سريع والديناميكي بطيء"},{"id":"C","text":"الثابت مجاني والديناميكي مدفوع"},{"id":"D","text":"لا يوجد فرق بينهما"}]'::jsonb,
'الثابت يدوي والديناميكي تلقائي', 'التوجيه الثابت (Static Routing) يتطلب من مدير الشبكة إدخال المسارات يدوياً في جدول التوجيه، بينما التوجيه الديناميكي (Dynamic Routing) يستخدم بروتوكولات مثل RIP أو OSPF لتحديث جدول التوجيه تلقائياً والتكيف مع التغيرات في الشبكة.', 'hard', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '68b8b6a8-407b-46f0-b22b-74bf5a43ecc1',
'أي من الخدمات التالية يمكن أن يوفرها الموجه؟', 'multiple_choice',
'[{"id":"A","text":"DHCP لتوزيع عناوين IP تلقائياً"},{"id":"B","text":"جدار ناري (Firewall) لحماية الشبكة"},{"id":"C","text":"VPN للاتصال الآمن"},{"id":"D","text":"جميع ما ذكر"}]'::jsonb,
'جميع ما ذكر', 'الموجهات الحديثة توفر مجموعة واسعة من الخدمات بما في ذلك: DHCP لتوزيع عناوين IP تلقائياً، جدار ناري لحماية الشبكة من التهديدات الخارجية، VPN لإنشاء اتصالات آمنة عبر الإنترنت، بالإضافة إلى خدمات NAT وQoS وغيرها.', 'hard', 10),

-- البطاقة 4: نقطة الوصول (Access Point)
('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما هي الوظيفة الأساسية لنقطة الوصول (Access Point)؟', 'multiple_choice',
'[{"id":"A","text":"ربط الأجهزة اللاسلكية بالشبكة السلكية"},{"id":"B","text":"ربط شبكتين سلكيتين"},{"id":"C","text":"تخزين البيانات"},{"id":"D","text":"طباعة المستندات"}]'::jsonb,
'ربط الأجهزة اللاسلكية بالشبكة السلكية', 'نقطة الوصول (Access Point أو AP) هي جهاز يعمل كجسر بين الأجهزة اللاسلكية (WiFi) والشبكة السلكية (Ethernet)، مما يسمح للأجهزة اللاسلكية بالاتصال بالشبكة المحلية والإنترنت.', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما الفرق بين نقطة الوصول (Access Point) والموجه اللاسلكي (Wireless Router)؟', 'multiple_choice',
'[{"id":"A","text":"نقطة الوصول تربط شبكات مختلفة، والموجه لا يفعل ذلك"},{"id":"B","text":"نقطة الوصول لا تحتوي على موجه مدمج"},{"id":"C","text":"لا يوجد فرق بينهما"},{"id":"D","text":"نقطة الوصول أسرع دائماً"}]'::jsonb,
'نقطة الوصول لا تحتوي على موجه مدمج', 'نقطة الوصول (AP) هي جهاز يوفر اتصال WiFi فقط ويجب توصيلها بموجه أو مُبْدِّل، بينما الموجه اللاسلكي (Wireless Router) يحتوي على موجه + نقطة وصول + مُبْدِّل مدمج في جهاز واحد، مما يجعله أكثر ملاءمة للاستخدام المنزلي.', 'easy', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما هو SSID في نقطة الوصول؟', 'multiple_choice',
'[{"id":"A","text":"اسم الشبكة اللاسلكية"},{"id":"B","text":"كلمة مرور الشبكة"},{"id":"C","text":"عنوان IP لنقطة الوصول"},{"id":"D","text":"نوع التشفير المستخدم"}]'::jsonb,
'اسم الشبكة اللاسلكية', 'SSID (Service Set Identifier) هو اسم الشبكة اللاسلكية الذي يظهر للمستخدمين عند البحث عن شبكات WiFi متاحة، ويمكن للمدير تغييره أو إخفاءه لأسباب أمنية.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما هو بروتوكول التشفير الأكثر أماناً لنقاط الوصول اللاسلكية حالياً؟', 'multiple_choice',
'[{"id":"A","text":"WEP"},{"id":"B","text":"WPA"},{"id":"C","text":"WPA2"},{"id":"D","text":"WPA3"}]'::jsonb,
'WPA3', 'WPA3 (Wi-Fi Protected Access 3) هو أحدث وأقوى بروتوكول تشفير للشبكات اللاسلكية، يوفر حماية أفضل من WPA2 ضد هجمات التخمين وهجمات القاموس، ويستخدم تشفير أقوى لحماية البيانات المنقولة.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما هي تقنية PoE المستخدمة مع نقاط الوصول؟', 'multiple_choice',
'[{"id":"A","text":"تقنية لزيادة السرعة"},{"id":"B","text":"تقنية لإمداد الطاقة عبر كابل الإيثرنت"},{"id":"C","text":"تقنية للتشفير"},{"id":"D","text":"تقنية للاتصال اللاسلكي"}]'::jsonb,
'تقنية لإمداد الطاقة عبر كابل الإيثرنت', 'PoE (Power over Ethernet) هي تقنية تسمح بإمداد نقطة الوصول بالطاقة الكهربائية والبيانات عبر نفس كابل الإيثرنت، مما يسهل تركيب نقاط الوصول في أماكن بعيدة عن مصادر الطاقة الكهربائية مثل الأسقف.', 'medium', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما هي الترددات الرئيسية المستخدمة في WiFi؟', 'multiple_choice',
'[{"id":"A","text":"2.4 GHz فقط"},{"id":"B","text":"5 GHz فقط"},{"id":"C","text":"2.4 GHz و 5 GHz"},{"id":"D","text":"1 GHz و 10 GHz"}]'::jsonb,
'2.4 GHz و 5 GHz', 'تستخدم شبكات WiFi بشكل رئيسي ترددين: 2.4 GHz (يوفر مدى أطول ولكن سرعة أقل وأكثر عرضة للتداخل) و 5 GHz (يوفر سرعة أعلى ومدى أقصر وتداخل أقل)، وتدعم الأجهزة الحديثة كلا الترددين (Dual-Band).', 'hard', 10),

('0eaba634-48f2-4e7e-a8e4-e8593eee848b', '4f9db10d-ed44-4687-8aed-c3719b52e2a9', '4fb776e4-6989-4e43-9413-b2aa6f8ab3cb',
'ما هو وضع العميل (Client Mode) في نقطة الوصول؟', 'multiple_choice',
'[{"id":"A","text":"تعمل كنقطة وصول عادية"},{"id":"B","text":"تتصل بنقطة وصول أخرى كجهاز عميل"},{"id":"C","text":"تعمل كموجه"},{"id":"D","text":"تعمل كمُبْدِّل"}]'::jsonb,
'تتصل بنقطة وصول أخرى كجهاز عميل', 'في وضع العميل (Client Mode أو Wireless Bridge)، تعمل نقطة الوصول كجهاز عميل يتصل بنقطة وصول أخرى لاسلكياً، مما يسمح بربط الأجهزة السلكية بشبكة لاسلكية بعيدة، وهذا مفيد لتمديد الشبكة أو ربط أجهزة غير قابلة للاتصال اللاسلكي.', 'hard', 10);