-- إضافة أسئلة البطاقات 5-7 (الأخيرة)
INSERT INTO grade11_game_questions 
(section_id, topic_id, lesson_id, question_text, question_type, choices, correct_answer, explanation, difficulty_level, points)
VALUES
-- البطاقة 5: عنوان MAC
('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'ما هو عنوان MAC؟', 'multiple_choice',
'[{"id":"A","text":"عنوان فيزيائي فريد لكل جهاز شبكي"},{"id":"B","text":"عنوان منطقي يمكن تغييره"},{"id":"C","text":"عنوان البريد الإلكتروني"},{"id":"D","text":"رقم الهاتف"}]'::jsonb,
'عنوان فيزيائي فريد لكل جهاز شبكي', 'عنوان MAC (Media Access Control Address) هو عنوان فيزيائي فريد يُعطى لكل بطاقة شبكة (Network Interface Card) من قبل الشركة المصنّعة، ويتكون من 48 بت (6 بايت) ويُكتب عادة بصيغة سداسية عشرية.', 'easy', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'كم عدد البايتات في عنوان MAC؟', 'multiple_choice',
'[{"id":"A","text":"4 بايت"},{"id":"B","text":"6 بايت"},{"id":"C","text":"8 بايت"},{"id":"D","text":"12 بايت"}]'::jsonb,
'6 بايت', 'عنوان MAC يتكون من 6 بايت (48 بت)، ويُكتب عادة على شكل 6 أزواج من الأرقام السداسية العشرية مفصولة بفواصل أو شرطات، مثل: AA:BB:CC:DD:EE:FF أو AA-BB-CC-DD-EE-FF.', 'easy', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'ما الذي تمثله أول 3 بايتات في عنوان MAC؟', 'multiple_choice',
'[{"id":"A","text":"رقم الجهاز"},{"id":"B","text":"معرّف الشركة المصنّعة (OUI)"},{"id":"C","text":"نوع الشبكة"},{"id":"D","text":"رقم المنفذ"}]'::jsonb,
'معرّف الشركة المصنّعة (OUI)', 'أول 3 بايتات (24 بت) من عنوان MAC تُسمى OUI (Organizationally Unique Identifier) وتحدد الشركة المصنّعة للجهاز، بينما الـ 3 بايتات الأخيرة يحددها المُصنّع لتمييز كل جهاز على حدة.', 'medium', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'في أي طبقة من نموذج OSI يُستخدم عنوان MAC؟', 'multiple_choice',
'[{"id":"A","text":"الطبقة الأولى (Physical)"},{"id":"B","text":"الطبقة الثانية (Data Link)"},{"id":"C","text":"الطبقة الثالثة (Network)"},{"id":"D","text":"الطبقة الرابعة (Transport)"}]'::jsonb,
'الطبقة الثانية (Data Link)', 'يُستخدم عنوان MAC في الطبقة الثانية (Data Link Layer) من نموذج OSI، وهي الطبقة المسؤولة عن الاتصال بين الأجهزة على نفس الشبكة المحلية باستخدام العناوين الفيزيائية.', 'medium', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'ما هو عنوان MAC الإذاعي (Broadcast MAC Address)؟', 'multiple_choice',
'[{"id":"A","text":"00:00:00:00:00:00"},{"id":"B","text":"FF:FF:FF:FF:FF:FF"},{"id":"C","text":"AA:AA:AA:AA:AA:AA"},{"id":"D","text":"11:11:11:11:11:11"}]'::jsonb,
'FF:FF:FF:FF:FF:FF', 'عنوان MAC الإذاعي (Broadcast MAC) هو FF:FF:FF:FF:FF:FF، ويُستخدم لإرسال البيانات إلى جميع الأجهزة على الشبكة المحلية، مثل عند البحث عن جهاز معين باستخدام بروتوكول ARP.', 'medium', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'هل يمكن تغيير عنوان MAC الفيزيائي للجهاز؟', 'multiple_choice',
'[{"id":"A","text":"لا، عنوان MAC ثابت ولا يمكن تغييره أبداً"},{"id":"B","text":"نعم، يمكن تغييره برمجياً (MAC Spoofing)"},{"id":"C","text":"نعم، ولكن فقط من قبل الشركة المصنّعة"},{"id":"D","text":"يتغير تلقائياً كل فترة"}]'::jsonb,
'نعم، يمكن تغييره برمجياً (MAC Spoofing)', 'رغم أن عنوان MAC يُحرق في بطاقة الشبكة من المصنع ويُفترض أن يكون ثابتاً، إلا أنه يمكن تغييره برمجياً من خلال نظام التشغيل (MAC Spoofing)، وهذا قد يُستخدم لأغراض مشروعة مثل الخصوصية أو لأغراض غير مشروعة مثل تجاوز فلاتر MAC.', 'hard', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', 'fa1b588e-852d-419b-a6e1-aed3a93b65e4', '983d841b-d89d-4dd1-9978-0549041e4933',
'ما الفرق الأساسي بين عنوان MAC وعنوان IP؟', 'multiple_choice',
'[{"id":"A","text":"MAC فيزيائي ثابت، IP منطقي ومتغير"},{"id":"B","text":"MAC للإنترنت، IP للشبكة المحلية"},{"id":"C","text":"MAC أطول من IP"},{"id":"D","text":"لا يوجد فرق بينهما"}]'::jsonb,
'MAC فيزيائي ثابت، IP منطقي ومتغير', 'عنوان MAC هو عنوان فيزيائي ثابت (Hardware Address) يُستخدم في الطبقة الثانية للاتصال المحلي، بينما عنوان IP هو عنوان منطقي (Logical Address) يُستخدم في الطبقة الثالثة ويمكن تغييره حسب الشبكة المتصل بها الجهاز، ويُستخدم للتوجيه بين الشبكات المختلفة.', 'hard', 10),

-- البطاقة 6: عنوان IP
('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'ما هو عنوان IP؟', 'multiple_choice',
'[{"id":"A","text":"عنوان منطقي لتحديد الأجهزة على الشبكة"},{"id":"B","text":"عنوان فيزيائي ثابت"},{"id":"C","text":"رقم المنفذ"},{"id":"D","text":"اسم الجهاز"}]'::jsonb,
'عنوان منطقي لتحديد الأجهزة على الشبكة', 'عنوان IP (Internet Protocol Address) هو عنوان منطقي فريد يُعطى لكل جهاز متصل بالشبكة، يُستخدم لتحديد موقع الجهاز وتوجيه البيانات إليه عبر الشبكات المختلفة.', 'easy', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'كم عدد البتات في عنوان IPv4؟', 'multiple_choice',
'[{"id":"A","text":"16 بت"},{"id":"B","text":"32 بت"},{"id":"C","text":"64 بت"},{"id":"D","text":"128 بت"}]'::jsonb,
'32 بت', 'عنوان IPv4 يتكون من 32 بت مقسمة إلى 4 أقسام (Octets)، كل قسم 8 بت، ويُكتب بصيغة عشرية نقطية (Dotted Decimal) مثل: 192.168.1.1، حيث قيمة كل قسم تتراوح من 0 إلى 255.', 'easy', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'أي من عناوين IP التالية هو عنوان خاص (Private IP)؟', 'multiple_choice',
'[{"id":"A","text":"8.8.8.8"},{"id":"B","text":"192.168.1.1"},{"id":"C","text":"142.250.185.46"},{"id":"D","text":"1.1.1.1"}]'::jsonb,
'192.168.1.1', '192.168.1.1 هو عنوان IP خاص (Private IP) يُستخدم داخل الشبكات المحلية فقط ولا يمكن الوصول إليه مباشرة من الإنترنت. نطاقات العناوين الخاصة هي: 10.0.0.0 - 10.255.255.255، 172.16.0.0 - 172.31.255.255، و 192.168.0.0 - 192.168.255.255.', 'medium', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'ما الفرق بين عنوان IP الثابت (Static) والديناميكي (Dynamic)؟', 'multiple_choice',
'[{"id":"A","text":"الثابت يُعطى يدوياً والديناميكي يُعطى تلقائياً"},{"id":"B","text":"الثابت أسرع من الديناميكي"},{"id":"C","text":"الديناميكي أكثر أماناً"},{"id":"D","text":"لا يوجد فرق بينهما"}]'::jsonb,
'الثابت يُعطى يدوياً والديناميكي يُعطى تلقائياً', 'عنوان IP الثابت (Static IP) يُعيّن يدوياً من قبل مدير الشبكة ولا يتغير، بينما عنوان IP الديناميكي (Dynamic IP) يُعطى تلقائياً من خادم DHCP ويمكن أن يتغير عند إعادة الاتصال. الثابت يُستخدم للخوادم والأجهزة المهمة، والديناميكي يُستخدم لأجهزة المستخدمين.', 'medium', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'ما هو عنوان Loopback في IPv4؟', 'multiple_choice',
'[{"id":"A","text":"192.168.0.1"},{"id":"B","text":"127.0.0.1"},{"id":"C","text":"0.0.0.0"},{"id":"D","text":"255.255.255.255"}]'::jsonb,
'127.0.0.1', 'عنوان Loopback هو 127.0.0.1 (أو أي عنوان من نطاق 127.0.0.0/8)، ويُستخدم للإشارة إلى الجهاز نفسه، مفيد لاختبار اتصال الشبكة المحلية على الجهاز دون الحاجة لشبكة فعلية.', 'medium', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'لماذا تم تطوير IPv6؟', 'multiple_choice',
'[{"id":"A","text":"لأن IPv4 أبطأ"},{"id":"B","text":"لنفاذ عناوين IPv4"},{"id":"C","text":"لتحسين التشفير فقط"},{"id":"D","text":"لتقليل التكلفة"}]'::jsonb,
'لنفاذ عناوين IPv4', 'تم تطوير IPv6 بشكل رئيسي لحل مشكلة نفاذ عناوين IPv4، حيث يوفر IPv4 حوالي 4.3 مليار عنوان فقط وهو غير كافٍ لعدد الأجهزة المتزايد، بينما IPv6 (128 بت) يوفر عدداً هائلاً من العناوين (3.4×10^38)، بالإضافة إلى تحسينات في الأمان والأداء.', 'hard', 10),

('9a7a101b-9272-4fd2-a572-5a535cefc129', '4295cd31-e59e-418d-bac6-a103355e0ac4', 'b888a5f5-571c-42d2-ac31-ebd33ee009de',
'ما هو الفرق بين Subnet Mask و Default Gateway؟', 'multiple_choice',
'[{"id":"A","text":"Subnet Mask تحدد الشبكة، Gateway يربط بشبكات أخرى"},{"id":"B","text":"لا يوجد فرق بينهما"},{"id":"C","text":"Subnet Mask للإنترنت، Gateway للشبكة المحلية"},{"id":"D","text":"Gateway أسرع من Subnet Mask"}]'::jsonb,
'Subnet Mask تحدد الشبكة، Gateway يربط بشبكات أخرى', 'Subnet Mask (قناع الشبكة الفرعية) يُستخدم لتقسيم عنوان IP إلى جزء الشبكة وجزء الجهاز، مما يحدد نطاق الشبكة المحلية. Default Gateway (البوابة الافتراضية) هو عنوان IP للموجه الذي يُستخدم للوصول إلى الشبكات والأجهزة خارج الشبكة المحلية، مثل الإنترنت.', 'hard', 10),

-- البطاقة 7: بروتوكول ARP
('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'ما هو بروتوكول ARP؟', 'multiple_choice',
'[{"id":"A","text":"بروتوكول لتحويل عنوان IP إلى عنوان MAC"},{"id":"B","text":"بروتوكول لتشفير البيانات"},{"id":"C","text":"بروتوكول لنقل الملفات"},{"id":"D","text":"بروتوكول للبريد الإلكتروني"}]'::jsonb,
'بروتوكول لتحويل عنوان IP إلى عنوان MAC', 'ARP (Address Resolution Protocol) هو بروتوكول يُستخدم لتحويل عنوان IP المنطقي إلى عنوان MAC الفيزيائي على الشبكة المحلية، وهو ضروري لأن الاتصال الفعلي في الطبقة الثانية يتم باستخدام عناوين MAC.', 'easy', 10),

('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'في أي طبقة من نموذج OSI يعمل بروتوكول ARP؟', 'multiple_choice',
'[{"id":"A","text":"الطبقة الأولى (Physical)"},{"id":"B","text":"بين الطبقة الثانية والثالثة"},{"id":"C","text":"الطبقة الرابعة (Transport)"},{"id":"D","text":"الطبقة السابعة (Application)"}]'::jsonb,
'بين الطبقة الثانية والثالثة', 'بروتوكول ARP يعمل بين الطبقة الثانية (Data Link - عناوين MAC) والطبقة الثالثة (Network - عناوين IP)، حيث يربط بين العناوين المنطقية والعناوين الفيزيائية.', 'easy', 10),

('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'كيف يعمل بروتوكول ARP عندما يريد جهاز إرسال بيانات لعنوان IP معين؟', 'multiple_choice',
'[{"id":"A","text":"يرسل طلب ARP إلى عنوان IP المطلوب مباشرة"},{"id":"B","text":"يرسل طلب ARP إذاعي (Broadcast) لجميع الأجهزة"},{"id":"C","text":"يرسل طلب إلى خادم DNS"},{"id":"D","text":"يرسل طلب إلى الموجه فقط"}]'::jsonb,
'يرسل طلب ARP إذاعي (Broadcast) لجميع الأجهزة', 'عندما يريد جهاز معرفة عنوان MAC لعنوان IP معين، يرسل ARP Request بشكل إذاعي (Broadcast) إلى جميع الأجهزة على الشبكة المحلية بالسؤال "من يملك هذا العنوان IP؟"، والجهاز المالك يرد بـ ARP Reply يحتوي على عنوان MAC الخاص به.', 'medium', 10),

('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'ما هو ARP Cache (جدول ARP)؟', 'multiple_choice',
'[{"id":"A","text":"جدول يخزن أسماء المواقع"},{"id":"B","text":"جدول يخزن تطابقات IP-MAC المكتشفة"},{"id":"C","text":"جدول يخزن كلمات المرور"},{"id":"D","text":"جدول يخزن المسارات"}]'::jsonb,
'جدول يخزن تطابقات IP-MAC المكتشفة', 'ARP Cache (أو ARP Table) هو جدول مؤقت يحتفظ به كل جهاز يحتوي على تطابقات (Mappings) بين عناوين IP وعناوين MAC المكتشفة مؤخراً، مما يوفر الوقت بتجنب إرسال طلبات ARP المتكررة لنفس العناوين.', 'medium', 10),

('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'ما هو Gratuitous ARP؟', 'multiple_choice',
'[{"id":"A","text":"طلب ARP للبحث عن جهاز معين"},{"id":"B","text":"رسالة ARP ينشرها الجهاز عن عنوان IP الخاص به"},{"id":"C","text":"رد على طلب ARP"},{"id":"D","text":"رسالة خطأ في ARP"}]'::jsonb,
'رسالة ARP ينشرها الجهاز عن عنوان IP الخاص به', 'Gratuitous ARP هو رسالة ARP يرسلها الجهاز من تلقاء نفسه (دون طلب) لإعلان جميع الأجهزة على الشبكة بعنوان IP و MAC الخاص به، يُستخدم للكشف عن تعارض عناوين IP أو لتحديث جداول ARP في الأجهزة الأخرى.', 'medium', 10),

('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'ما هو هجوم ARP Spoofing (ARP Poisoning)؟', 'multiple_choice',
'[{"id":"A","text":"هجوم لتعطيل خدمة ARP"},{"id":"B","text":"هجوم يرسل فيه المهاجم ردود ARP مزيفة"},{"id":"C","text":"هجوم لسرقة عناوين IP"},{"id":"D","text":"هجوم لتشفير حركة المرور"}]'::jsonb,
'هجوم يرسل فيه المهاجم ردود ARP مزيفة', 'ARP Spoofing (أو ARP Poisoning) هو هجوم أمني يرسل فيه المهاجم رسائل ARP مزيفة لربط عنوان MAC الخاص به بعنوان IP لجهاز آخر (مثل البوابة الافتراضية)، مما يسمح له بالتنصت على حركة المرور أو تعديلها (Man-in-the-Middle Attack).', 'hard', 10),

('b49b4b78-a88c-484a-9638-bd7b948b828b', '40823cff-cb90-4402-9e38-44923b7ab09b', 'a7856553-dd8f-436c-a504-e0e98d452e27',
'ما هو بروتوكول RARP؟', 'multiple_choice',
'[{"id":"A","text":"بروتوكول عكس ARP - يحول MAC إلى IP"},{"id":"B","text":"بروتوكول محسّن لـ ARP"},{"id":"C","text":"بروتوكول بديل لـ ARP"},{"id":"D","text":"بروتوكول لإلغاء ARP"}]'::jsonb,
'بروتوكول عكس ARP - يحول MAC إلى IP', 'RARP (Reverse ARP) هو بروتوكول عكس ARP، يُستخدم لتحويل عنوان MAC إلى عنوان IP، كان يُستخدم في الأجهزة عديمة القرص (Diskless Workstations) للحصول على عنوان IP عند بدء التشغيل، ولكنه الآن تم استبداله ببروتوكولات أحدث مثل BOOTP و DHCP.', 'hard', 10);