('55555555-5555-5555-5555-555555555555', '944d7533-f1b6-4161-89cd-dbe7ec67b1b7', 'أساسيات الإيثرنت ومجالات الشبكة', 300, true),
('66666666-6666-6666-6666-666666666666', 'ac8390e2-8dae-49a0-84e3-610730d3db7d', 'تصميم الشبكات ورزم البيانات', 301, true);

-- إضافة الأسئلة (10 لكل بطاقة)
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
-- البطاقة الأولى: أساسيات الإيثرنت ومجالات الشبكة
('55555555-5555-5555-5555-555555555555', 'ما هي الإيثرنت (Ethernet)؟', 'multiple_choice', '["تقنية شبكات محلية سلكية تربط الأجهزة في نطاق جغرافي محدود", "بروتوكول للإنترنت اللاسلكي فقط", "نوع من الكابلات الضوئية", "جهاز توجيه للشبكات الواسعة"]'::jsonb, 'تقنية شبكات محلية سلكية تربط الأجهزة في نطاق جغرافي محدود', 'easy', 10),

('55555555-5555-5555-5555-555555555555', 'أي من التالي يعتبر من مميزات شبكات الإيثرنت؟', 'multiple_choice', '["سهولة التركيب والتكلفة المنخفضة", "تعمل فقط لاسلكياً", "لا تحتاج إلى كابلات", "تستخدم فقط في الشبكات الواسعة"]'::jsonb, 'سهولة التركيب والتكلفة المنخفضة', 'easy', 10),

('55555555-5555-5555-5555-555555555555', 'ما المقصود بمجال التصادم (Collision Domain)؟', 'multiple_choice', '["منطقة في الشبكة حيث يمكن أن تتصادم حزم البيانات عند إرسالها في نفس الوقت", "منطقة يتم فيها بث البيانات لجميع الأجهزة", "جهاز يمنع دخول الفيروسات", "كابل خاص بالشبكات السريعة"]'::jsonb, 'منطقة في الشبكة حيث يمكن أن تتصادم حزم البيانات عند إرسالها في نفس الوقت', 'medium', 10),

('55555555-5555-5555-5555-555555555555', 'ما هو مجال البث (Broadcast Domain)؟', 'multiple_choice', '["مجموعة من الأجهزة التي تستقبل رسائل البث المرسلة من أي جهاز في المجموعة", "نوع من الكابلات عالية السرعة", "برنامج لإدارة الشبكات", "جهاز لتوزيع الإنترنت"]'::jsonb, 'مجموعة من الأجهزة التي تستقبل رسائل البث المرسلة من أي جهاز في المجموعة', 'medium', 10),

('55555555-5555-5555-5555-555555555555', 'ما الفرق الرئيسي بين Collision Domain و Broadcast Domain؟', 'multiple_choice', '["Collision Domain يتعلق بالتصادمات المادية للبيانات، بينما Broadcast Domain يتعلق بانتشار رسائل البث", "لا يوجد فرق بينهما، هما نفس المفهوم", "Broadcast Domain أصغر دائماً من Collision Domain", "Collision Domain خاص بالشبكات اللاسلكية فقط"]'::jsonb, 'Collision Domain يتعلق بالتصادمات المادية للبيانات، بينما Broadcast Domain يتعلق بانتشار رسائل البث', 'hard', 10),

('55555555-5555-5555-5555-555555555555', 'أي جهاز يقوم بفصل مجالات التصادم (Collision Domains)؟', 'multiple_choice', '["Switch (المحول)", "Hub (المُجمِّع)", "Cable (الكابل)", "Repeater (المُكرر)"]'::jsonb, 'Switch (المحول)', 'easy', 10),

('55555555-5555-5555-5555-555555555555', 'عند استخدام Hub في الشبكة، كم عدد مجالات التصادم (Collision Domains) التي يتم إنشاؤها؟', 'multiple_choice', '["مجال تصادم واحد فقط لجميع المنافذ", "مجال تصادم لكل منفذ", "لا يوجد مجال تصادم مع Hub", "مجالان فقط بغض النظر عن عدد المنافذ"]'::jsonb, 'مجال تصادم واحد فقط لجميع المنافذ', 'medium', 10),

('55555555-5555-5555-5555-555555555555', 'أي جهاز يقوم بفصل مجالات البث (Broadcast Domains)؟', 'multiple_choice', '["Router (الموجه)", "Switch فقط", "Hub فقط", "Repeater فقط"]'::jsonb, 'Router (الموجه)', 'medium', 10),

('55555555-5555-5555-5555-555555555555', 'ما هو المعيار القياسي IEEE للإيثرنت؟', 'multiple_choice', '["IEEE 802.3", "IEEE 802.11", "IEEE 802.15", "IEEE 802.16"]'::jsonb, 'IEEE 802.3', 'easy', 10),

('55555555-5555-5555-5555-555555555555', 'في شبكة تحتوي على Switch واحد متصل به 24 جهاز كمبيوتر، كم عدد Collision Domains و Broadcast Domains على الترتيب؟', 'multiple_choice', '["24 مجال تصادم، مجال بث واحد", "مجال تصادم واحد، 24 مجال بث", "24 مجال تصادم، 24 مجال بث", "مجال تصادم واحد، مجال بث واحد"]'::jsonb, '24 مجال تصادم، مجال بث واحد', 'hard', 10),

-- البطاقة الثانية: تصميم الشبكات ورزم البيانات
('66666666-6666-6666-6666-666666666666', 'كم عدد الطبقات الرئيسية في التصميم الهرمي لشبكات سيسكو؟', 'multiple_choice', '["ثلاث طبقات: Core و Distribution و Access", "طبقتان فقط", "أربع طبقات", "خمس طبقات"]'::jsonb, 'ثلاث طبقات: Core و Distribution و Access', 'easy', 10),

('66666666-6666-6666-6666-666666666666', 'ما هي الوظيفة الرئيسية لطبقة Core في التصميم الهرمي؟', 'multiple_choice', '["نقل البيانات بسرعة عالية بين طبقات Distribution المختلفة", "توصيل المستخدمين النهائيين بالشبكة", "تطبيق سياسات الأمان والفلترة", "تخزين البيانات"]'::jsonb, 'نقل البيانات بسرعة عالية بين طبقات Distribution المختلفة', 'medium', 10),

('66666666-6666-6666-6666-666666666666', 'ما هي طبقة Access في التصميم الهرمي؟', 'multiple_choice', '["الطبقة التي توفر الاتصال المباشر للأجهزة والمستخدمين النهائيين", "الطبقة المسؤولة عن التوجيه بين الشبكات", "الطبقة التي تربط بين مراكز البيانات", "الطبقة التي تدير قواعد البيانات"]'::jsonb, 'الطبقة التي توفر الاتصال المباشر للأجهزة والمستخدمين النهائيين', 'medium', 10),

('66666666-6666-6666-6666-666666666666', 'ما هي رزمة بيانات Broadcast؟', 'multiple_choice', '["رزمة يتم إرسالها إلى جميع الأجهزة في الشبكة", "رزمة تُرسل لجهاز واحد محدد فقط", "رزمة تُرسل لمجموعة محددة من الأجهزة", "رزمة لا يمكن إرسالها في الشبكة"]'::jsonb, 'رزمة يتم إرسالها إلى جميع الأجهزة في الشبكة', 'easy', 10),

('66666666-6666-6666-6666-666666666666', 'ما هو عنوان MAC المستخدم لرسائل Broadcast؟', 'multiple_choice', '["FF:FF:FF:FF:FF:FF", "00:00:00:00:00:00", "01:01:01:01:01:01", "AA:AA:AA:AA:AA:AA"]'::jsonb, 'FF:FF:FF:FF:FF:FF', 'easy', 10),

('66666666-6666-6666-6666-666666666666', 'ما المقصود بـ Half Duplex في الإيثرنت؟', 'multiple_choice', '["الإرسال والاستقبال يتم في اتجاه واحد فقط في كل لحظة", "الإرسال والاستقبال يتم في الاتجاهين في نفس الوقت", "لا يمكن الإرسال أو الاستقبال", "الإرسال فقط بدون استقبال"]'::jsonb, 'الإرسال والاستقبال يتم في اتجاه واحد فقط في كل لحظة', 'medium', 10),

('66666666-6666-6666-6666-666666666666', 'ما هي الميزة الرئيسية لـ Full Duplex مقارنة بـ Half Duplex؟', 'multiple_choice', '["إمكانية الإرسال والاستقبال في نفس الوقت مما يضاعف سرعة النقل الفعلية", "استهلاك طاقة أقل فقط", "تكلفة أقل فقط", "لا توجد تصادمات ولكن سرعة النقل لا تتغير"]'::jsonb, 'إمكانية الإرسال والاستقبال في نفس الوقت مما يضاعف سرعة النقل الفعلية', 'hard', 10),

('66666666-6666-6666-6666-666666666666', 'ما هي أشهر سرعات الإيثرنت المستخدمة حالياً؟', 'multiple_choice', '["10 Mbps، 100 Mbps، 1 Gbps، 10 Gbps", "1 Kbps، 10 Kbps، 100 Kbps فقط", "500 Mbps، 750 Mbps، 900 Mbps فقط", "5 Gbps، 15 Gbps، 25 Gbps فقط"]'::jsonb, '10 Mbps، 100 Mbps، 1 Gbps، 10 Gbps', 'medium', 10),

('66666666-6666-6666-6666-666666666666', 'عند إرسال رزمة بيانات من PC إلى PC آخر في نفس الشبكة المحلية، ما هو العنوان الذي يتم استخدامه للتوجيه؟', 'multiple_choice', '["عنوان MAC (Media Access Control)", "عنوان IP فقط", "عنوان البريد الإلكتروني", "اسم الجهاز فقط"]'::jsonb, 'عنوان MAC (Media Access Control)', 'hard', 10),

('66666666-6666-6666-6666-666666666666', 'ما هي وظيفة طبقة Distribution في التصميم الهرمي؟', 'multiple_choice', '["تطبيق السياسات والتوجيه بين VLANs وتجميع الاتصالات من طبقة Access", "توصيل المستخدمين النهائيين فقط", "نقل البيانات بسرعة عالية فقط دون فلترة", "تخزين النسخ الاحتياطية"]'::jsonb, 'تطبيق السياسات والتوجيه بين VLANs وتجميع الاتصالات من طبقة Access', 'easy', 10);