INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
('33333333-3333-3333-3333-333333333333', 'ما هو الأمر لتكوين IP على منفذ في الراوتر؟', 'multiple_choice', '[{"id": "a", "text": "set ip address"}, {"id": "b", "text": "ip address [ip] [subnet]"}, {"id": "c", "text": "configure ip"}, {"id": "d", "text": "address ip [ip]"}]', 'ip address [ip] [subnet]', 'medium', 10),
('33333333-3333-3333-3333-333333333333', 'كيف يتم تفعيل منفذ في الراوتر؟', 'multiple_choice', '[{"id": "a", "text": "enable interface"}, {"id": "b", "text": "no shutdown"}, {"id": "c", "text": "activate port"}, {"id": "d", "text": "interface up"}]', 'no shutdown', 'easy', 10),
('33333333-3333-3333-3333-333333333333', 'ما هو الأمر للدخول إلى تكوين منفذ معين؟', 'multiple_choice', '[{"id": "a", "text": "interface [type] [number]"}, {"id": "b", "text": "config interface [name]"}, {"id": "c", "text": "port [number]"}, {"id": "d", "text": "select interface"}]', 'interface [type] [number]', 'easy', 10),
('33333333-3333-3333-3333-333333333333', 'ما هي أنواع المنافذ الشائعة في الراوتر؟', 'multiple_choice', '[{"id": "a", "text": "Ethernet و Serial"}, {"id": "b", "text": "USB و HDMI"}, {"id": "c", "text": "VGA و DVI"}, {"id": "d", "text": "Parallel و PS/2"}]', 'Ethernet و Serial', 'easy', 10),
('33333333-3333-3333-3333-333333333333', 'ما هو الأمر لعرض جدول التوجيه؟', 'multiple_choice', '[{"id": "a", "text": "show routes"}, {"id": "b", "text": "display routing"}, {"id": "c", "text": "show ip route"}, {"id": "d", "text": "routing table"}]', 'show ip route', 'easy', 10),
('33333333-3333-3333-3333-333333333333', 'ما هو الأمر لإضافة مسار ثابت (static route)؟', 'multiple_choice', '[{"id": "a", "text": "add route"}, {"id": "b", "text": "ip route [network] [mask] [next-hop]"}, {"id": "c", "text": "route add"}, {"id": "d", "text": "static route"}]', 'ip route [network] [mask] [next-hop]', 'medium', 10),
('33333333-3333-3333-3333-333333333333', 'ما هو Clock Rate ومتى يُستخدم؟', 'multiple_choice', '[{"id": "a", "text": "سرعة المعالج"}, {"id": "b", "text": "يُعيّن على DCE في الاتصالات Serial"}, {"id": "c", "text": "سرعة النقل في Ethernet"}, {"id": "d", "text": "وقت النظام"}]', 'يُعيّن على DCE في الاتصالات Serial', 'hard', 10),
('33333333-3333-3333-3333-333333333333', 'ما هو الأمر لعرض معلومات تفصيلية عن منفذ معين؟', 'multiple_choice', '[{"id": "a", "text": "show interface [name]"}, {"id": "b", "text": "display port [name]"}, {"id": "c", "text": "interface info"}, {"id": "d", "text": "port details"}]', 'show interface [name]', 'easy', 10),
('33333333-3333-3333-3333-333333333333', 'ماذا يعني "administratively down" لمنفذ ما؟', 'multiple_choice', '[{"id": "a", "text": "المنفذ معطل يدوياً"}, {"id": "b", "text": "المنفذ تالف"}, {"id": "c", "text": "لا يوجد كابل"}, {"id": "d", "text": "المنفذ مشغول"}]', 'المنفذ معطل يدوياً', 'medium', 10),
('33333333-3333-3333-3333-333333333333', 'ما هو الأمر لإضافة وصف (description) لمنفذ؟', 'multiple_choice', '[{"id": "a", "text": "set description"}, {"id": "b", "text": "description [text]"}, {"id": "c", "text": "port name"}, {"id": "d", "text": "label [text]"}]', 'description [text]', 'easy', 10);
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
('44444444-4444-4444-4444-444444444444', 'ما هو الأمر لتفعيل بروتوكول RIP للتوجيه؟', 'multiple_choice', '[{"id": "a", "text": "router rip"}, {"id": "b", "text": "enable rip"}, {"id": "c", "text": "rip protocol"}, {"id": "d", "text": "routing rip"}]', 'router rip', 'medium', 10),
('44444444-4444-4444-4444-444444444444', 'كيف يتم إضافة شبكة في بروتوكول RIP؟', 'multiple_choice', '[{"id": "a", "text": "add network [ip]"}, {"id": "b", "text": "network [ip]"}, {"id": "c", "text": "route network"}, {"id": "d", "text": "rip network [ip]"}]', 'network [ip]', 'medium', 10),
('44444444-4444-4444-4444-444444444444', 'ما هو الفرق بين RIPv1 و RIPv2؟', 'multiple_choice', '[{"id": "a", "text": "RIPv2 يدعم VLSM والتشفير"}, {"id": "b", "text": "RIPv1 أسرع"}, {"id": "c", "text": "لا يوجد فرق"}, {"id": "d", "text": "RIPv1 للشبكات الكبيرة"}]', 'RIPv2 يدعم VLSM والتشفير', 'hard', 10),
('44444444-4444-4444-4444-444444444444', 'ما هو الأمر لعرض بروتوكولات التوجيه النشطة؟', 'multiple_choice', '[{"id": "a", "text": "show protocols"}, {"id": "b", "text": "show ip protocols"}, {"id": "c", "text": "display routing"}, {"id": "d", "text": "active protocols"}]', 'show ip protocols', 'medium', 10),
('44444444-4444-4444-4444-444444444444', 'ما هو Default Route؟', 'multiple_choice', '[{"id": "a", "text": "المسار الأسرع"}, {"id": "b", "text": "المسار المستخدم للوجهات غير المعروفة"}, {"id": "c", "text": "المسار الأول"}, {"id": "d", "text": "المسار الأساسي"}]', 'المسار المستخدم للوجهات غير المعروفة', 'medium', 10),
('44444444-4444-4444-4444-444444444444', 'كيف يتم إنشاء Default Route؟', 'multiple_choice', '[{"id": "a", "text": "ip route 0.0.0.0 0.0.0.0 [next-hop]"}, {"id": "b", "text": "default route [ip]"}, {"id": "c", "text": "route default"}, {"id": "d", "text": "set default-route"}]', 'ip route 0.0.0.0 0.0.0.0 [next-hop]', 'hard', 10),
('44444444-4444-4444-4444-444444444444', 'ما هو NAT (Network Address Translation)؟', 'multiple_choice', '[{"id": "a", "text": "تشفير البيانات"}, {"id": "b", "text": "ترجمة عناوين IP بين الشبكات"}, {"id": "c", "text": "توزيع الأحمال"}, {"id": "d", "text": "فلترة الحزم"}]', 'ترجمة عناوين IP بين الشبكات', 'medium', 10),
('44444444-4444-4444-4444-444444444444', 'ما هو الأمر لتفعيل NAT على منفذ داخلي؟', 'multiple_choice', '[{"id": "a", "text": "nat inside"}, {"id": "b", "text": "ip nat inside"}, {"id": "c", "text": "enable nat"}, {"id": "d", "text": "nat enable inside"}]', 'ip nat inside', 'hard', 10),
('44444444-4444-4444-4444-444444444444', 'ما الفرق بين Static NAT و Dynamic NAT؟', 'multiple_choice', '[{"id": "a", "text": "Static NAT تعيين ثابت، Dynamic تعيين ديناميكي"}, {"id": "b", "text": "لا يوجد فرق"}, {"id": "c", "text": "Static أسرع"}, {"id": "d", "text": "Dynamic للشبكات الصغيرة"}]', 'Static NAT تعيين ثابت، Dynamic تعيين ديناميكي', 'hard', 10),
('44444444-4444-4444-4444-444444444444', 'ما هو الأمر لعرض ترجمات NAT النشطة؟', 'multiple_choice', '[{"id": "a", "text": "show nat"}, {"id": "b", "text": "display nat translations"}, {"id": "c", "text": "show ip nat translations"}, {"id": "d", "text": "nat show"}]', 'show ip nat translations', 'medium', 10);