INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
('55555555-5555-5555-5555-555555555555', 'ما هو نظام التشغيل الأكثر شيوعاً في أجهزة Cisco؟', 'multiple_choice', '[{"id": "a", "text": "Windows"}, {"id": "b", "text": "Linux"}, {"id": "c", "text": "IOS (Internetwork Operating System)"}, {"id": "d", "text": "Unix"}]', 'IOS (Internetwork Operating System)', 'easy', 10),
('55555555-5555-5555-5555-555555555555', 'أين يتم تخزين نظام التشغيل IOS؟', 'multiple_choice', '[{"id": "a", "text": "RAM"}, {"id": "b", "text": "ROM"}, {"id": "c", "text": "Flash Memory"}, {"id": "d", "text": "NVRAM"}]', 'Flash Memory', 'medium', 10),
('55555555-5555-5555-5555-555555555555', 'ما هي مراحل إقلاع (Boot) الراوتر بالترتيب؟', 'multiple_choice', '[{"id": "a", "text": "POST - Bootstrap - IOS - Configuration"}, {"id": "b", "text": "IOS - POST - Configuration"}, {"id": "c", "text": "Configuration - IOS - POST"}, {"id": "d", "text": "Bootstrap - POST - IOS"}]', 'POST - Bootstrap - IOS - Configuration', 'hard', 10),
('55555555-5555-5555-5555-555555555555', 'ما هو POST (Power-On Self-Test)؟', 'multiple_choice', '[{"id": "a", "text": "فحص ذاتي للعتاد عند التشغيل"}, {"id": "b", "text": "تحميل نظام التشغيل"}, {"id": "c", "text": "حفظ التكوين"}, {"id": "d", "text": "إعادة التشغيل"}]', 'فحص ذاتي للعتاد عند التشغيل', 'medium', 10),
('55555555-5555-5555-5555-555555555555', 'ما هي وظيفة Bootstrap في عملية الإقلاع؟', 'multiple_choice', '[{"id": "a", "text": "تحميل نظام التشغيل"}, {"id": "b", "text": "فحص العتاد"}, {"id": "c", "text": "حفظ الإعدادات"}, {"id": "d", "text": "توفير الطاقة"}]', 'تحميل نظام التشغيل', 'medium', 10),
('55555555-5555-5555-5555-555555555555', 'ما هو Configuration Register؟', 'multiple_choice', '[{"id": "a", "text": "سجل يتحكم في عملية الإقلاع"}, {"id": "b", "text": "ملف التكوين"}, {"id": "c", "text": "نوع من الذاكرة"}, {"id": "d", "text": "أداة برمجية"}]', 'سجل يتحكم في عملية الإقلاع', 'hard', 10),
('55555555-5555-5555-5555-555555555555', 'ما هي القيمة الافتراضية لـ Configuration Register؟', 'multiple_choice', '[{"id": "a", "text": "0x2102"}, {"id": "b", "text": "0x2142"}, {"id": "c", "text": "0x0000"}, {"id": "d", "text": "0xFFFF"}]', '0x2102', 'hard', 10),
('55555555-5555-5555-5555-555555555555', 'ما هو الأمر لعرض محتويات Flash Memory؟', 'multiple_choice', '[{"id": "a", "text": "show memory"}, {"id": "b", "text": "display flash"}, {"id": "c", "text": "show flash"}, {"id": "d", "text": "list flash"}]', 'show flash', 'easy', 10),
('55555555-5555-5555-5555-555555555555', 'كيف يمكن نسخ IOS جديد إلى Flash؟', 'multiple_choice', '[{"id": "a", "text": "copy tftp flash"}, {"id": "b", "text": "upload ios"}, {"id": "c", "text": "install ios"}, {"id": "d", "text": "flash update"}]', 'copy tftp flash', 'medium', 10),
('55555555-5555-5555-5555-555555555555', 'ما هو TFTP؟', 'multiple_choice', '[{"id": "a", "text": "بروتوكول نقل الملفات البسيط"}, {"id": "b", "text": "نوع من الذاكرة"}, {"id": "c", "text": "نظام تشغيل"}, {"id": "d", "text": "بروتوكول توجيه"}]', 'بروتوكول نقل الملفات البسيط', 'easy', 10);
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
('66666666-6666-6666-6666-666666666666', 'ما هو ROM Monitor Mode؟', 'multiple_choice', '[{"id": "a", "text": "وضع الطوارئ لاستعادة IOS"}, {"id": "b", "text": "الوضع العادي للتشغيل"}, {"id": "c", "text": "وضع التكوين"}, {"id": "d", "text": "وضع الصيانة البسيطة"}]', 'وضع الطوارئ لاستعادة IOS', 'hard', 10),
('66666666-6666-6666-6666-666666666666', 'كيف يمكن الدخول إلى ROM Monitor Mode؟', 'multiple_choice', '[{"id": "a", "text": "بالضغط على Ctrl+Break أثناء الإقلاع"}, {"id": "b", "text": "بأمر rommon"}, {"id": "c", "text": "تلقائياً عند فشل IOS"}, {"id": "d", "text": "كلاً من a و c"}]', 'كلاً من a و c', 'hard', 10),
('66666666-6666-6666-6666-666666666666', 'ما هو الأمر لعرض Configuration Register الحالي؟', 'multiple_choice', '[{"id": "a", "text": "show config-register"}, {"id": "b", "text": "show version"}, {"id": "c", "text": "display register"}, {"id": "d", "text": "config-register show"}]', 'show version', 'medium', 10),
('66666666-6666-6666-6666-666666666666', 'ما هي القيمة المستخدمة لتجاوز startup-config؟', 'multiple_choice', '[{"id": "a", "text": "0x2102"}, {"id": "b", "text": "0x2142"}, {"id": "c", "text": "0x0000"}, {"id": "d", "text": "0x2100"}]', '0x2142', 'hard', 10),
('66666666-6666-6666-6666-666666666666', 'كيف يتم تحديث نظام IOS؟', 'multiple_choice', '[{"id": "a", "text": "نسخ ملف IOS جديد إلى Flash"}, {"id": "b", "text": "تنزيل من الإنترنت مباشرة"}, {"id": "c", "text": "تثبيت من CD"}, {"id": "d", "text": "التحديث التلقائي"}]', 'نسخ ملف IOS جديد إلى Flash', 'medium', 10),
('66666666-6666-6666-6666-666666666666', 'ما هو الأمر لحذف ملف من Flash؟', 'multiple_choice', '[{"id": "a", "text": "delete flash:[filename]"}, {"id": "b", "text": "remove [filename]"}, {"id": "c", "text": "erase [filename]"}, {"id": "d", "text": "clear flash"}]', 'delete flash:[filename]', 'medium', 10),
('66666666-6666-6666-6666-666666666666', 'ما هي العوامل التي يجب مراعاتها عند ترقية IOS؟', 'multiple_choice', '[{"id": "a", "text": "حجم Flash المتاح وحجم RAM"}, {"id": "b", "text": "اللون الخارجي للجهاز"}, {"id": "c", "text": "عدد المنافذ"}, {"id": "d", "text": "سرعة المعالج فقط"}]', 'حجم Flash المتاح وحجم RAM', 'medium', 10),
('66666666-6666-6666-6666-666666666666', 'ما هو CDP (Cisco Discovery Protocol)؟', 'multiple_choice', '[{"id": "a", "text": "بروتوكول لاكتشاف أجهزة Cisco المجاورة"}, {"id": "b", "text": "بروتوكول توجيه"}, {"id": "c", "text": "بروتوكول أمان"}, {"id": "d", "text": "بروتوكول نقل ملفات"}]', 'بروتوكول لاكتشاف أجهزة Cisco المجاورة', 'medium', 10),
('66666666-6666-6666-6666-666666666666', 'ما هو الأمر لعرض معلومات CDP عن الأجهزة المجاورة؟', 'multiple_choice', '[{"id": "a", "text": "show cdp"}, {"id": "b", "text": "show cdp neighbors"}, {"id": "c", "text": "display neighbors"}, {"id": "d", "text": "cdp show"}]', 'show cdp neighbors', 'easy', 10),
('66666666-6666-6666-6666-666666666666', 'لماذا قد يتم تعطيل CDP لأسباب أمنية؟', 'multiple_choice', '[{"id": "a", "text": "يكشف معلومات عن الشبكة للمهاجمين"}, {"id": "b", "text": "يبطئ الشبكة"}, {"id": "c", "text": "يستهلك الذاكرة"}, {"id": "d", "text": "غير متوافق مع البروتوكولات الأخرى"}]', 'يكشف معلومات عن الشبكة للمهاجمين', 'medium', 10);
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
('77777777-7777-7777-7777-777777777777', 'ما هي الطريقة الأساسية لتأمين الوصول إلى السويتش؟', 'multiple_choice', '[{"id": "a", "text": "إخفاء السويتش"}, {"id": "b", "text": "تعيين كلمات مرور قوية"}, {"id": "c", "text": "عدم استخدام السويتش"}, {"id": "d", "text": "فصل الكهرباء"}]', 'تعيين كلمات مرور قوية', 'easy', 10),
('77777777-7777-7777-7777-777777777777', 'ما هو Port Security؟', 'multiple_choice', '[{"id": "a", "text": "تقنية للتحكم في الأجهزة المسموح لها بالاتصال بالمنفذ"}, {"id": "b", "text": "تشفير البيانات"}, {"id": "c", "text": "حماية من الفيروسات"}, {"id": "d", "text": "جدار ناري"}]', 'تقنية للتحكم في الأجهزة المسموح لها بالاتصال بالمنفذ', 'medium', 10),
('77777777-7777-7777-7777-777777777777', 'ما هو الأمر لتفعيل Port Security على منفذ؟', 'multiple_choice', '[{"id": "a", "text": "enable port-security"}, {"id": "b", "text": "switchport port-security"}, {"id": "c", "text": "port security on"}, {"id": "d", "text": "secure port"}]', 'switchport port-security', 'medium', 10),
('77777777-7777-7777-7777-777777777777', 'كيف يمكن تحديد الحد الأقصى لعناوين MAC المسموحة على منفذ؟', 'multiple_choice', '[{"id": "a", "text": "max-mac [number]"}, {"id": "b", "text": "switchport port-security maximum [number]"}, {"id": "c", "text": "limit mac [number]"}, {"id": "d", "text": "mac-limit [number]"}]', 'switchport port-security maximum [number]', 'medium', 10),
('77777777-7777-7777-7777-777777777777', 'ما هي أوضاع Violation في Port Security؟', 'multiple_choice', '[{"id": "a", "text": "Protect, Restrict, Shutdown"}, {"id": "b", "text": "Allow, Deny, Drop"}, {"id": "c", "text": "Accept, Reject, Ignore"}, {"id": "d", "text": "Enable, Disable, Monitor"}]', 'Protect, Restrict, Shutdown', 'hard', 10),
('77777777-7777-7777-7777-777777777777', 'ماذا يحدث في وضع Shutdown عند حدوث Violation؟', 'multiple_choice', '[{"id": "a", "text": "يتم تعطيل المنفذ"}, {"id": "b", "text": "يتم تجاهل الحزمة فقط"}, {"id": "c", "text": "يتم تسجيل الحدث فقط"}, {"id": "d", "text": "لا شيء"}]', 'يتم تعطيل المنفذ', 'medium', 10),
('77777777-7777-7777-7777-777777777777', 'كيف يمكن تحديد عنوان MAC ثابت (Sticky) في Port Security؟', 'multiple_choice', '[{"id": "a", "text": "switchport port-security mac-address sticky"}, {"id": "b", "text": "mac sticky"}, {"id": "c", "text": "static mac"}, {"id": "d", "text": "fixed mac"}]', 'switchport port-security mac-address sticky', 'hard', 10),
('77777777-7777-7777-7777-777777777777', 'ما هو الأمر لعرض إعدادات Port Security؟', 'multiple_choice', '[{"id": "a", "text": "show port-security"}, {"id": "b", "text": "display security"}, {"id": "c", "text": "show port-security interface [name]"}, {"id": "d", "text": "security show"}]', 'show port-security interface [name]', 'easy', 10),
('77777777-7777-7777-7777-777777777777', 'كيف يمكن استعادة منفذ تم إيقافه بسبب violation؟', 'multiple_choice', '[{"id": "a", "text": "restart port"}, {"id": "b", "text": "shutdown ثم no shutdown"}, {"id": "c", "text": "reset port"}, {"id": "d", "text": "enable port"}]', 'shutdown ثم no shutdown', 'medium', 10),
('77777777-7777-7777-7777-777777777777', 'لماذا يُعتبر Port Security مهماً؟', 'multiple_choice', '[{"id": "a", "text": "يمنع الوصول غير المصرح به والهجمات"}, {"id": "b", "text": "يزيد السرعة"}, {"id": "c", "text": "يوفر الطاقة"}, {"id": "d", "text": "يقلل التكلفة"}]', 'يمنع الوصول غير المصرح به والهجمات', 'easy', 10);
INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
