('88888888-8888-8888-8888-888888888888', 'ما هو Native VLAN Attack؟', 'multiple_choice', '[{"id": "a", "text": "هجوم يستغل VLAN الافتراضي"}, {"id": "b", "text": "هجوم على الشبكات الأصلية"}, {"id": "c", "text": "فيروس"}, {"id": "d", "text": "برمجية خبيثة"}]', 'هجوم يستغل VLAN الافتراضي', 'hard', 10),
('88888888-8888-8888-8888-888888888888', 'كيف يمكن الحماية من Native VLAN Attack؟', 'multiple_choice', '[{"id": "a", "text": "تغيير Native VLAN عن الافتراضي (VLAN 1)"}, {"id": "b", "text": "حذف جميع VLANs"}, {"id": "c", "text": "تعطيل Trunking"}, {"id": "d", "text": "استخدام كلمات مرور قوية فقط"}]', 'تغيير Native VLAN عن الافتراضي (VLAN 1)', 'hard', 10);