('c6666666-6666-6666-6666-666666666666', 'كيف يتم تحديث نظام IOS؟', 'multiple_choice', '[{"id": "a", "text": "نسخ ملف IOS جديد إلى Flash"}, {"id": "b", "text": "تنزيل من الإنترنت مباشرة"}, {"id": "c", "text": "تثبيت من CD"}, {"id": "d", "text": "التحديث التلقائي"}]', 'نسخ ملف IOS جديد إلى Flash', 'medium', 10),
('c6666666-6666-6666-6666-666666666666', 'ما هو الأمر لحذف ملف من Flash؟', 'multiple_choice', '[{"id": "a", "text": "delete flash:[filename]"}, {"id": "b", "text": "remove [filename]"}, {"id": "c", "text": "erase [filename]"}, {"id": "d", "text": "clear flash"}]', 'delete flash:[filename]', 'medium', 10),
