INSERT INTO grade11_game_questions (lesson_id, question_text, question_type, choices, correct_answer, difficulty_level, points) VALUES
('c7777777-7777-7777-7777-777777777777', 'ما هي الطريقة الأساسية لتأمين الوصول إلى السويتش؟', 'multiple_choice', '[{"id": "a", "text": "إخفاء السويتش"}, {"id": "b", "text": "تعيين كلمات مرور قوية"}, {"id": "c", "text": "عدم استخدام السويتش"}, {"id": "d", "text": "فصل الكهرباء"}]', 'تعيين كلمات مرور قوية', 'easy', 10),
